mapper.select:
SELECT board_name, board_title
FROM board
WHERE board_idx = #{board_idx}

mapper.update:
UPDATE board
SET count = count + 1
WHERE content_idx = #{content_idx}

UPDATE board
SET title = #{title},
content = #{content}
WHERE content_idx = #{content_idx}

mapper.select:
SELECT COUNT(*)
FROM board
WHERE board_idx = #{board_idx}
